import React, { Component } from "react";
import "./App.css";
import TodoList from "./components/TodoList";
import TodoItems from "./components/TodoItems";
import TodoCount from "./components/TodoCount";
// import ShowTime from "./components/ShowTime";
import TodoTitle from "./components/TodoTitle";

class App extends Component {
  state = {
    items: [],
    currentItem: {
      text: "",
      key: "",
      state: ""
    }
  };

  constructor() {
    console.log("APP Constructor");
    super();
    this.state = {
      items: [],
      currentItem: { text: "", key: "", state: "" }
    };
  }

  // retrieve todo items from local storage after components have mounted
  componentDidMount() {
    console.log("APP Components Did Mount");

    const items = JSON.parse(localStorage.getItem("todos"));
    if (items !== null) {
      this.setState({ items: items });
    }
  }

  // save todo items to local storage
  saveStateToLocalStorage() {
    localStorage.setItem("todos", JSON.stringify(this.state.items));
    console.log("Saved to local");
  }

  // perform this if there is change in input box
  handleInput = e => {
    const itemText = e.target.value;
    const currentItem = { text: itemText, key: Date.now(), state: "ndone" };
    this.setState({ currentItem });
  };

  // add a todo item
  addItem = e => {
    e.preventDefault();
    console.log("Add Item");

    const newItem = this.state.currentItem;
    if (newItem.text !== "") {
      console.log(newItem);
      const items = [...this.state.items, newItem];
      this.setState(
        {
          items: items,
          currentItem: { text: "", key: "", state: "" }
        },
        () => {
          this.saveStateToLocalStorage();
        }
      );
    }
    console.log("New Task Added", newItem.text);
  };

  // edit 'this' todo item
  editItem = key => {
    var nname = prompt("Enter any name");

    var oname = this.state.items.filter(item => {
      return item.key === key;
    })[0].text;

    this.state.items.filter(item => {
      return item.key === key;
    })[0].text = nname;

    this.setState(this.state.items, () => {
      this.saveStateToLocalStorage();
    });
    console.log("Task ", oname, "updated to ", nname);
  };

  // deletes this todo item
  deleteItem = key => {
    const filteredItems = this.state.items.filter(item => {
      return item.key !== key;
    });
    this.setState(
      {
        items: filteredItems
      },
      () => {
        this.saveStateToLocalStorage();
      }
    );
    console.log("Task Deleted");
  };

  // delete todos
  deleteTodos = option => {
    // 0 - all
    // 1 - not done
    // 2 - done
    switch (option) {
      case 0:
        // let resetTodo = [];
        this.setState(
          {
            items: []
          },
          () => {
            this.saveStateToLocalStorage();
          }
        );
        break;
      case 1:
        const doneTodos = this.state.items.filter(item => {
          return item.state === "done";
        });
        console.log(doneTodos);
        // this.setState(
        //   {
        //     items: doneTodos
        //   },
        //   () => {
        //     this.saveStateToLocalStorage();
        //   }
        // );
        break;
      case 2:
        let ndoneTodos = this.state.items.filter(item => {
          return item.state === "ndone";
        });
        // this.setState(
        //   {
        //     items: ndoneTodos
        //   },
        //   () => {
        //     this.saveStateToLocalStorage();
        //   }
        // );
        break;
      default:
        console.log("Invalid options");
    }
  };

  // toggles done and not done for 'this' todo item
  toggleDone = key => {
    var to_toggle = this.state.items.filter(item => {
      return item.key === key;
    })[0];

    if (to_toggle.state === "ndone") {
      to_toggle.state = "done";
    } else {
      to_toggle.state = "ndone";
    }

    this.setState(this.state.items, () => {
      this.saveStateToLocalStorage();
    });
    console.log("Task State Toggled");
  };

  search = e => {
    console.log("search");
    console.log(e.target.value);
    console.log(document.getElementById("filter").value);
    var filter_state = document.getElementById("filter").value;
    var filter_value = "";

    if (filter_state === "1") {
      filter_value = "done";
    } else if (filter_state === "2") {
      filter_value = "ndone";
    } else {
      filter_value = "";
    }
    console.log(filter_value);
    var query = e.target.value;
    console.log(document.getElementsByTagName("span"));
    let todos = document.getElementsByTagName("span");
    for (var i = 0; i < todos.length; i++) {
      var root_todo = todos[i].parentNode.parentNode;
      if (filter_value === "") {
        if (
          !todos[i].textContent.toLowerCase().startsWith(query.toLowerCase())
        ) {
          root_todo.setAttribute("style", "display:none");
        } else {
          // scount++;
          root_todo.removeAttribute("style");
        }
      } else {
        if (
          !todos[i].textContent.toLowerCase().startsWith(query.toLowerCase()) ||
          root_todo.getAttribute("state") !== filter_value
        ) {
          root_todo.setAttribute("style", "display:none");
        } else {
          // scount++;
          root_todo.removeAttribute("style");
        }
      }
    }
  };

  // clearsearch = e => {
  //   console.log("triggrere on blur");
  //   console.log(document.getElementById("search"));
  //   document.getElementById("search").nodeValue = "";
  // };

  filter = e => {
    // 0 - clear filter
    // 1 - done filter
    // 2 - not done filter

    let filter = Number(e.target.value);
    console.log(typeof filter);
    console.log("this bvalue", document.getElementById("search").value);

    let todos = document.getElementsByTagName("span");

    switch (filter) {
      case 0:
        for (let i = 0; i < todos.length; i++) {
          let root_todo = todos[i].parentNode.parentNode;
          root_todo.removeAttribute("style");
        }
        break;

      case 1:
        for (let i = 0; i < todos.length; i++) {
          let root_todo = todos[i].parentNode.parentNode;
          console.log(root_todo.getAttribute("state"));
          if (root_todo.getAttribute("state") !== "done") {
            root_todo.setAttribute("style", "display:none");
          } else {
            root_todo.removeAttribute("style");
          }
        }
        break;
      case 2:
        for (let i = 0; i < todos.length; i++) {
          let root_todo = todos[i].parentNode.parentNode;
          console.log(root_todo.getAttribute("state"));
          if (root_todo.getAttribute("state") !== "ndone") {
            root_todo.setAttribute("style", "display:none");
          } else {
            root_todo.removeAttribute("style");
          }
        }
        break;
      default:
    }

    console.log("filter");
  };

  // render all components
  render() {
    console.log("APP Rendered");
    return (
      <React.Fragment>
        {/* <ShowTime /> */}

        <div className="App">
          <TodoTitle />

          <TodoList
            addItem={this.addItem}
            inputElement={this.inputElement}
            handleInput={this.handleInput}
            currentItem={this.state.currentItem}
            search={this.search}
            clearsearch={this.clearsearch}
            filter={this.filter}
          />

          <hr />

          <TodoCount entries={this.state.items} delete={this.deleteTodos} />

          <TodoItems
            entries={this.state.items}
            editItem={this.editItem}
            deleteItem={this.deleteItem}
            toggleDone={this.toggleDone}
          />
        </div>
      </React.Fragment>
    );
  }
}

export default App;
