import React, { Component } from "react";
import "./TodoList.css";

class TodoCount extends Component {
  render() {
    console.log("TodoCount rendered");
    var ntodo = this.props.entries.length;

    var done_count = this.props.entries.filter(item => {
      return item.state === "done";
    }).length;

    var ndone_count = this.props.entries.filter(item => {
      return item.state === "ndone";
    }).length;

    return (
      <div>
        <center>
          <h3>
            TASKS
            <div className="black">
              {/* <anything onClick={this.props.delete(0)}> */}
              .:
              {ntodo}
              :.
              {/* </a> */}
              {/* </anything> */}
            </div>
          </h3>
          <b className="rbold">
            TODO {/* <a onClick={this.props.delete(1)}> */}
            .:
            {ndone_count}
            :.
            {/* </a> */}
          </b>
          &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
          <b className="gbold">
            DONE {/* <a onClick={this.props.delete(2)}> */}
            .:
            {done_count}
            :.
            {/* </a> */}
          </b>
          <br />
          <br />
        </center>
      </div>
    );
  }
}

export default TodoCount;
