import React, { Component } from "react";
import "./App.css";
import TodoList from "./components/TodoList";
import TodoItems from "./components/TodoItems";

class App extends Component {
  state = {
    items: [],
    currentItem: {
      text: "",
      key: "",
      state: ""
    }
  };

  constructor() {
    console.log("APP Constructor");
    super();
    this.state = {
      items: [],
      currentItem: { text: "", key: "", state: "" }
    };
  }

  // retrieve todo items from local storage after components have mounted
  componentDidMount() {
    console.log("APP Components Did Mount");

    const items = JSON.parse(localStorage.getItem(1));
    this.setState({ items: items });
  }

  // save todo items to local storage
  saveStateToLocalStorage() {
    localStorage.setItem(1, JSON.stringify(this.state.items));
    console.log("Saved to local");
  }

  // perform this if there is change in input box
  handleInput = e => {
    const itemText = e.target.value;
    const currentItem = { text: itemText, key: Date.now(), state: "ndone" };
    this.setState({ currentItem });
  };

  // add a todo item
  addItem = e => {
    e.preventDefault();
    console.log("Add Item");

    const newItem = this.state.currentItem;
    if (newItem.text !== "") {
      console.log(newItem);
      const items = [...this.state.items, newItem];
      this.setState(
        {
          items: items,
          currentItem: { text: "", key: "", state: "" }
        },
        () => {
          this.saveStateToLocalStorage();
        }
      );
    }
  };

  // edit 'this' todo item
  editItem = key => {
    var nname = prompt("Enter any name");

    this.state.items.filter(item => {
      return item.key === key;
    })[0].text = nname;

    this.setState(this.state.items, () => {
      this.saveStateToLocalStorage();
    });
  };

  // deletes this todo item
  deleteItem = key => {
    const filteredItems = this.state.items.filter(item => {
      return item.key !== key;
    });
    this.setState(
      {
        items: filteredItems
      },
      () => {
        this.saveStateToLocalStorage();
      }
    );
  };

  // toggles done and not done for 'this' todo item
  toggleDone = key => {
    var to_toggle = this.state.items.filter(item => {
      return item.key === key;
    })[0];

    if (to_toggle.state === "ndone") {
      to_toggle.state = "done";
    } else {
      to_toggle.state = "ndone";
    }

    this.setState(this.state.items, () => {
      this.saveStateToLocalStorage();
    });
  };

  // render all components
  render() {
    console.log("APP Rendered");
    return (
      <div className="App">
        <TodoList
          addItem={this.addItem}
          inputElement={this.inputElement}
          handleInput={this.handleInput}
          currentItem={this.state.currentItem}
        />

        <TodoItems
          entries={this.state.items}
          editItem={this.editItem}
          deleteItem={this.deleteItem}
          toggleDone={this.toggleDone}
        />
      </div>
    );
  }
}

export default App;
