import React, { Component } from "react";

class TodoItem extends Component {
  render() {
    console.log(this);
    return (
      <div>
        <span key={this.props.item.key}>{this.props.item.text}</span>
        <button
          onClick={() => this.props.editItem(this.key)}
          className="btn btn-danger btn-sm m-2"
        >
          Edit
        </button>
        <button
          onClick={() => this.props.deleteItem(this.key)}
          className="btn btn-danger btn-sm m-2"
        >
          Delete
        </button>
        <button
          onClick={() => this.props.toggleDone(this.key)}
          className="btn btn-danger btn-sm m-2"
        >
          Done
        </button>
      </div>
    );
  }
}

export default TodoItem;
